import React from 'react';
import {
  Card,
  CardBody,
  CardFooter,
  Text,
  Image,
  Stack,
  Heading,
  Divider,
  Button,
  Select,
  Box,
  Center,
  Flex,
} from '@chakra-ui/react';

const PizzaCard = ({ imageSrc, pizzaName, prices }) => {
  const [selectedSize, setSelectedSize] = React.useState('small'); // Default to 'small'
  const [selectedQuantity, setSelectedQuantity] = React.useState(1); // Default to 1

  // Function to calculate total price based on selected size and quantity
  const calculateTotalPrice = () => {
    const price = prices[selectedSize];
    return price * selectedQuantity;
  };

  const handleSizeChange = (event) => {
    setSelectedSize(event.target.value);
  };

  const handleQuantityChange = (event) => {
    setSelectedQuantity(parseInt(event.target.value));
  };

  const totalPrice = calculateTotalPrice();

  return (
    <Card width='350px' height='450px' mb='4' border='1px' borderColor='gray.200'>
      <CardBody>
        <Center>
          <Image src={imageSrc} alt={pizzaName} borderRadius='lg' height='200px' />
        </Center>
        <Stack mt='4' spacing='3'>
          <Heading size='md' textAlign='center'>{pizzaName}</Heading>
          <Stack direction='row' justifyContent='space-between' alignItems='center'>
            <Stack>
              <Text>Variants:</Text>
              <Select value={selectedSize} onChange={handleSizeChange} size='sm'>
                <option value=''>Select size</option>
                <option value='small'>Small</option>
                <option value='medium'>Medium</option>
                <option value='large'>Large</option>
              </Select>
            </Stack>
            <Stack>
              <Text>Quantity:</Text>
              <Select value={selectedQuantity} onChange={handleQuantityChange} size='sm'>
                <option value={1}>1</option>
                <option value={2}>2</option>
                <option value={3}>3</option>
                {/* Add more options as needed */}
              </Select>
            </Stack>
          </Stack>
          {selectedSize && (
            <Flex justifyContent='space-between' alignItems='center'>
              <Text fontSize='xl'>
                Price: {totalPrice} Rs/-
              </Text>
              <Button colorScheme='red' fontSize='lg' padding='1rem'>
                ADD TO CART
              </Button>
            </Flex>
          )}
        </Stack>
      </CardBody>
    </Card>
  );
};

const Main = () => {
  // Prices for each size category
  const prices = {
    small: 200,
    medium: 300,
    large: 400,
  };

  return (
    <Box display='flex' flexWrap='wrap' justifyContent='center' gap='40px'>
      <PizzaCard
        imageSrc='https://www.dominos.co.in/files/items/Non-Veg_Supreme.jpg'
        pizzaName='Non Veg Supreme'
        prices={prices}
      />
      <PizzaCard
        imageSrc='https://www.dominos.co.in/files/items/Non-Veg_Supreme.jpg'
        pizzaName='Golden Corn Pizza'
        prices={prices}
      />
      <PizzaCard
        imageSrc='https://www.dominos.co.in/files/items/Non-Veg_Supreme.jpg'
        pizzaName='Jalapeno & Red Paprika Pizza'
        prices={prices}
      />
      <PizzaCard
        imageSrc='https://www.dominos.co.in/files/items/Non-Veg_Supreme.jpg'
        pizzaName='Jalapeno & Red Paprika Pizza'
        prices={prices}
      />
      {/* Add more PizzaCard components as needed */}
    </Box>
  );
};

export default Main;
