import React, { useState } from 'react';
import {
  Card,
  CardBody,
  Text,
  Image,
  Stack,
  Heading,
  Button,
  Select,
  Box,
  Center,
  Flex,
} from '@chakra-ui/react';
import PizzaDetailsModal from '../pizzaDetails/pizzaDetails'; // Import the new modal component

const PizzaCard = ({ imageSrc, pizzaName, prices, description }) => {
  const [selectedSize, setSelectedSize] = useState('small'); // Default to 'small'
  const [selectedQuantity, setSelectedQuantity] = useState(1); // Default to 1
  const [isModalOpen, setIsModalOpen] = useState(false); // State to handle modal visibility

  // Function to calculate total price based on selected size and quantity
  const calculateTotalPrice = () => {
    const price = prices[selectedSize];
    return price * selectedQuantity;
  };

  const handleSizeChange = (event) => {
    setSelectedSize(event.target.value);
  };

  const handleQuantityChange = (event) => {
    setSelectedQuantity(parseInt(event.target.value));
  };

  const totalPrice = calculateTotalPrice();

  return (
    <Card width="350px" height="450px" mb="4" border="1px" borderColor="gray.200" boxShadow=" 5px 10px 8px 2px rgba(0, 0, 0, 0.2)">
      <CardBody>
        <Center>
          <Image
            src={imageSrc}
            alt={pizzaName}
            borderRadius="lg"
            height="200px"
            cursor="pointer"
            onClick={() => setIsModalOpen(true)} // Open modal on image click
          />
        </Center>
        <Stack mt="4" spacing="3">
          <Heading size="md" textAlign="center">
            {pizzaName}
          </Heading>
          <Stack direction="row" justifyContent="space-between" alignItems="center">
            <Stack>
              <Text>Variants:</Text>
              <Select value={selectedSize} onChange={handleSizeChange} size="sm">
                <option value="small">Small</option>
                <option value="medium">Medium</option>
                <option value="large">Large</option>
              </Select>
            </Stack>
            <Stack>
              <Text>Quantity:</Text>
              <Select value={selectedQuantity} onChange={handleQuantityChange} size="sm">
                <option value={1}>1</option>
                <option value={2}>2</option>
                <option value={3}>3</option>
              </Select>
            </Stack>
          </Stack>
          {selectedSize && (
            <Flex justifyContent="space-between" alignItems="center">
              <Text fontSize="xl">Price: {totalPrice} Rs/-</Text>
              <Button colorScheme="red" fontSize="lg" padding="1rem">
                ADD TO CART
              </Button>
            </Flex>
          )}
        </Stack>
      </CardBody>
      <PizzaDetailsModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        pizza={{ name: pizzaName, imageSrc, description }}
      />
    </Card>
  );
};

export default PizzaCard;
