import React from 'react';
import {
  Card,
  CardBody,
  Text,
  Image,
  Stack,
  Heading,
  Button,
  Select,
  Center,
  Flex,
  Input,
} from '@chakra-ui/react';

const PizzaCard = ({ imageSrc, pizzaName, prices }) => {
  const [selectedSize, setSelectedSize] = React.useState('small');
  const [selectedQuantity, setSelectedQuantity] = React.useState(1);
  const [customQuantity, setCustomQuantity] = React.useState('');

  const calculateTotalPrice = () => {
    const price = prices[selectedSize];
    const quantity = selectedQuantity === 'custom' ? parseInt(customQuantity) || 0 : selectedQuantity;
    return price * quantity;
  };

  const handleSizeChange = (event) => {
    setSelectedSize(event.target.value);
  };

  const handleQuantityChange = (event) => {
    const value = event.target.value;
    if (value === 'custom') {
      setSelectedQuantity('custom');
    } else {
      setSelectedQuantity(parseInt(value));
      setCustomQuantity('');
    }
  };

  const handleCustomQuantityChange = (event) => {
    setCustomQuantity(event.target.value);
  };

  const totalPrice = calculateTotalPrice();

  return (
    <Card width='350px' height='auto' mb='4' border='1px' borderColor='gray.200' boxShadow='5px 10px 50px 2px rgba(0, 0, 0, 0.2)'>
      <CardBody>
        <Center>
          <Image src={imageSrc} alt={pizzaName} borderRadius='lg' height='200px' />
        </Center>
        <Stack mt='4' spacing='3'>
          <Heading size='md' textAlign='center'>{pizzaName}</Heading>
          <Stack direction='row' justifyContent='space-between' alignItems='center'>
            <Stack>
              <Text>Variants:</Text>
              <Select value={selectedSize} onChange={handleSizeChange} size='sm'>
                <option value='small'>Small</option>
                <option value='medium'>Medium</option>
                <option value='large'>Large</option>
              </Select>
            </Stack>
            <Stack>
              <Text>Quantity:</Text>
              <Select value={selectedQuantity} onChange={handleQuantityChange} size='sm'>
                <option value={1}>1</option>
                <option value={2}>2</option>
                <option value={3}>3</option>
                <option value='custom'>Custom</option>
              </Select>
              {selectedQuantity === 'custom' && (
                <Input
                  value={customQuantity}
                  onChange={handleCustomQuantityChange}
                  placeholder='Enter quantity'
                  size='sm'
                  type='number'
                  min={1}
                />
              )}
            </Stack>
          </Stack>
          {selectedSize && (
            <Flex justifyContent='space-between' alignItems='center'>
              <Text fontSize='xl'>
                Price: {totalPrice} Rs/-
              </Text>
              <Button colorScheme='red' fontSize='lg' padding='1rem'>
                ADD TO CART
              </Button>
            </Flex>
          )}
        </Stack>
      </CardBody>
    </Card>
  );
};

export default PizzaCard;
