import React from 'react';
import {
  Card,
  CardBody,
  CardFooter,
  Text,
  Image,
  Stack,
  Heading,
  Divider,
  ButtonGroup,
  Button,
  Select,
} from '@chakra-ui/react';

const PizzaCard = ({ imageSrc, pizzaName, basePrice }) => {
  const [selectedSize, setSelectedSize] = React.useState('');
  const [selectedQuantity, setSelectedQuantity] = React.useState(1);

  const handleSizeChange = (event) => {
    setSelectedSize(event.target.value);
  };

  const handleQuantityChange = (event) => {
    setSelectedQuantity(parseInt(event.target.value));
  };

  const totalPrice = basePrice * selectedQuantity;

  return (
    <Card maxW='490px' maxH='400px' mb='4'>
      <CardBody>
        <Image src={imageSrc} alt={pizzaName} borderRadius='lg' />
        <Stack mt='4' spacing='3'>
          <Heading size='md'>{pizzaName}</Heading>
          <Stack direction='row' justifyContent='space-between'>
            <Stack>
              <Text>Size:</Text>
              <Select value={selectedSize} onChange={handleSizeChange} size='sm'>
                <option value='small'>Small</option>
                <option value='medium'>Medium</option>
                <option value='large'>Large</option>
              </Select>
            </Stack>
            <Stack>
              <Text>Quantity:</Text>
              <Select value={selectedQuantity} onChange={handleQuantityChange} size='sm'>
                <option value={1}>1</option>
                <option value={2}>2</option>
                <option value={3}>3</option>
                {/* Add more options as needed */}
              </Select>
            </Stack>
          </Stack>
          <Text color='blue.600' fontSize='2xl'>
            Total Price: ${totalPrice.toFixed(2)}
          </Text>
        </Stack>
      </CardBody>
      <Divider />
      <CardFooter>
        <ButtonGroup spacing='2'>
          <Button variant='solid' colorScheme='blue'>
            Buy now
          </Button>
          <Button variant='ghost' colorScheme='blue'>
            Add to cart
          </Button>
        </ButtonGroup>
      </CardFooter>
    </Card>
  );
};

const Main = () => {
  return (
    <div className='Main' style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
      <PizzaCard
        imageSrc='https://www.dominos.co.in/files/items/Non-Veg_Supreme.jpg'
        pizzaName='Pizza Name 1'
        basePrice={10} // Set the base price for the first pizza
      />

      <PizzaCard
        imageSrc='https://www.dominos.co.in/files/items/Non-Veg_Supreme.jpg'
        pizzaName='Pizza Name 2'
        basePrice={12} // Set the base price for the second pizza
      />
    </div>
  );
};

export default Main;
