import React from 'react';
import {
  Card,
  CardBody,
  CardFooter,
  Text,
  Image,
  Stack,
  Heading,
  Divider,
  Button,
  Select,
  Box,
} from '@chakra-ui/react';

const PizzaCard = ({ imageSrc, pizzaName, prices }) => {
  const [selectedSize, setSelectedSize] = React.useState('');
  const [selectedQuantity, setSelectedQuantity] = React.useState(1);

  // Function to calculate total price based on selected size and quantity
  const calculateTotalPrice = () => {
    if (!selectedSize) return 0;

    const price = prices[selectedSize];
    return price * selectedQuantity;
  };

  const handleSizeChange = (event) => {
    setSelectedSize(event.target.value);
  };

  const handleQuantityChange = (event) => {
    setSelectedQuantity(parseInt(event.target.value));
  };

  const totalPrice = calculateTotalPrice();

  return (
    <Card maxW='300px' mb='4' border='1px' borderColor='gray.200'>
      <CardBody>
        <Image src={imageSrc} alt={pizzaName} borderRadius='lg' />
        <Stack mt='4' spacing='3'>
          <Heading size='md' textAlign='center'>{pizzaName}</Heading>
          <Stack direction='row' justifyContent='space-between' alignItems='center'>
            <Stack>
              <Text>Variants:</Text>
              <Select value={selectedSize} onChange={handleSizeChange} size='sm'>
                <option value=''>Select size</option>
                <option value='small'>Small</option>
                <option value='medium'>Medium</option>
                <option value='large'>Large</option>
              </Select>
            </Stack>
            <Stack>
              <Text>Quantity:</Text>
              <Select value={selectedQuantity} onChange={handleQuantityChange} size='sm'>
                <option value={1}>1</option>
                <option value={2}>2</option>
                <option value={3}>3</option>
                {/* Add more options as needed */}
              </Select>
            </Stack>
          </Stack>
          {selectedSize && (
            <Text fontSize='xl' textAlign='center'>
              Price: {totalPrice} Rs/-
            </Text>
          )}
        </Stack>
      </CardBody>
      <Divider />
      <CardFooter display='flex' justifyContent='center'>
        <Button colorScheme='red' width='100%'>
          ADD TO CART
        </Button>
      </CardFooter>
    </Card>
  );
};

const Main = () => {
  // Prices for each size category
  const prices = {
    small: 200,
    medium: 300,
    large: 400,
  };

  return (
    <Box display='flex' gap='20px' flexWrap='wrap' justifyContent='center'>
      <PizzaCard
        imageSrc='https://www.dominos.co.in/files/items/Non-Veg_Supreme.jpg'
        pizzaName='Non Veg Supreme'
        prices={prices}
      />
    </Box>
  );
};

export default Main;
